local M={}; function M.save_json(p,t) local h=fs.open(p,"w"); h.write(textutils.serializeJSON(t,true)); h.close() end; function M.load_json(p,d) if not fs.exists(p) then return d end local h=fs.open(p,"r"); local s=h.readAll(); h.close(); local ok,t=pcall(textutils.unserializeJSON,s); if ok and type(t)=="table" then return setmetatable(t,{__index=d}) else return d end end; return M